import React, { useState, useRef } from 'react';
import { toast } from 'sonner';
import GeminiService from '../utils/geminiService';
import VoiceInput from './VoiceInput';

const ChatInput = ({ 
  onNewQuestion, 
  isGenerating,
  onPauseOrTerminate
}) => {
  const [question, setQuestion] = useState('');
  const [attachedFile, setAttachedFile] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isVoiceMode, setIsVoiceMode] = useState(false);
  const fileInputRef = useRef(null);

  // File Validation
  const validateFile = (file) => {
    const allowedTypes = [
      'image/jpeg', 
      'image/png', 
      'image/gif', 
      'application/pdf',
      'image/webp',
      'image/svg+xml'
    ];
    const maxSize = 10 * 1024 * 1024; // 10MB
    const minSize = 1 * 1024; // 1KB

    if (!allowedTypes.includes(file.type)) {
      toast.error('Unsupported file type');
      return false;
    }

    if (file.size > maxSize) {
      toast.error('File too large (max 10MB)');
      return false;
    }

    if (file.size < minSize) {
      toast.error('File too small');
      return false;
    }

    return true;
  };

  // File Change Handler
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file && validateFile(file)) {
      setAttachedFile(file);
    } else {
      e.target.value = '';
    }
  };

  // Remove Attached File
  const handleFileRemove = () => {
    setAttachedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = '';
    }
  };

  // Convert File to Base64
  const readFileAsBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  // Submit Handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Ensure we're not in voice mode
    setIsVoiceMode(false);

    const trimmedQuestion = question.trim();
    if (!trimmedQuestion && !attachedFile) return;

    if (trimmedQuestion.length > 1000) {
      toast.error('Question is too long. Maximum 1000 characters.');
      return;
    }

    setIsLoading(true);
    try {
      let result;
      if (attachedFile) {
        const fileContent = await readFileAsBase64(attachedFile);
        result = await GeminiService.generateAnswerWithFile(
          trimmedQuestion, 
          fileContent
        );
      } else {
        result = await GeminiService.generateAnswer(trimmedQuestion);
      }
      
      if (result.success) {
        onNewQuestion(trimmedQuestion, result.answer);
      } else {
        const errorMessage = result.error || 
          "An unexpected error occurred while processing your request.";
        
        toast.error(errorMessage);
        onNewQuestion(trimmedQuestion, errorMessage);
      }
    } catch (error) {
      const userFriendlyMessage = 
        error.message.includes('API key') 
          ? "Invalid or missing API key. Please check your configuration." 
          : error.message.includes('network')
          ? "Network error. Please check your internet connection."
          : "An unexpected error occurred. Please try again later.";
      
      toast.error(userFriendlyMessage);
      onNewQuestion(trimmedQuestion, userFriendlyMessage);
    } finally {
      setIsLoading(false);
      setQuestion('');
      handleFileRemove();
    }
  };

  // Toggle Voice Mode
  const handleVoiceToggle = () => {
    setIsVoiceMode(!isVoiceMode);
  };

  return (
    <div className="bg-white dark:bg-gray-800 p-4 rounded-lg shadow-md">
      <form onSubmit={handleSubmit} className="space-y-2">
        <div className="flex items-center space-x-2">
          {/* File Attachment Button */}
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleFileChange}
            className="hidden"
            accept="image/jpeg,image/png,image/gif,application/pdf,image/webp,image/svg+xml"
          />
          
          <button
            type="button"
            onClick={() => fileInputRef.current?.click()}
            className="p-2 bg-gray-200 dark:bg-gray-700 rounded 
                       hover:bg-gray-300 dark:hover:bg-gray-600 
                       transition-colors"
            aria-label="Attach File"
          >
            📎
          </button>

          {/* Attached File Display */}
          {attachedFile && (
            <div className="flex items-center bg-blue-50 dark:bg-blue-900/30 px-2 py-1 rounded">
              <span className="text-sm mr-2 text-gray-600 dark:text-gray-300">
                {attachedFile.name}
              </span>
              <button
                type="button"
                onClick={handleFileRemove}
                className="text-red-500 hover:text-red-700"
              >
                ✕
              </button>
            </div>
          )}

          {/* Voice Toggle Button */}
          <button
            type="button"
            onClick={handleVoiceToggle}
            className={`p-2 rounded-md transition-colors 
              ${isVoiceMode 
                ? 'bg-blue-500 text-white' 
                : 'bg-gray-200 dark:bg-gray-700 text-gray-700 dark:text-gray-300'}`}
          >
            🎤 Voice
          </button>

          {/* Text/Voice Input Area */}
          {isVoiceMode ? (
            <VoiceInput
              onNewQuestion={onNewQuestion}
              setIsLoading={setIsLoading}
              onPauseOrTerminate={onPauseOrTerminate}
            />
          ) : (
            <input
              type="text"
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="Ask a question... (max 1000 chars)"
              maxLength={1000}
              className="flex-grow px-4 py-2 border dark:border-gray-600 
                         focus:outline-none focus:ring-2 focus:ring-blue-500 
                         dark:bg-gray-700"
            />
          )}

          {/* Send Button (Only Active for Text Input) */}
          {!isVoiceMode && (
            <button
              type="submit"
              disabled={!question.trim() || isLoading}
              className={`bg-blue-500 hover:bg-blue-600 text-white 
                         px-4 py-2 rounded transition-colors
                         ${!question.trim() || isLoading 
                           ? 'opacity-50 cursor-not-allowed' 
                           : ''}`}
            >
              Send
            </button>
          )}
        </div>

        {/* Character Count */}
        {!isVoiceMode && question.length > 0 && (
          <div className="text-xs text-gray-500 self-end">
            {question.length}/1000 characters
          </div>
        )}
      </form>
    </div>
  );
};

export default ChatInput;
