import React, { useState } from 'react';
import Navbar from './components/Navbar';
import ChatInput from './components/ChatInput';
import ResponseDisplay from './components/ResponseDisplay';
import { ThemeProvider } from './context/ThemeContext';

function App() {
  // State for conversation history
  const [conversation, setConversation] = useState([]);

  // State for loading
  const [isLoading, setIsLoading] = useState(false);

  // State for text generation
  const [isGenerating, setIsGenerating] = useState(false);

  // State for voice input
  const [isSpeaking, setIsSpeaking] = useState(false);

  // Function to handle new questions (text or voice)
  const handleNewQuestion = (question, answer) => {
    setConversation(prev => [...prev, { question, answer }]);
  };

  // Function to pause/terminate text generation
  const handlePauseOrTerminate = () => {
    setIsGenerating(false); // Stop text generation
  };

  // Function to stop voice input
  const handleStopSpeaking = () => {
    setIsSpeaking(false); // Stop voice input
  };

  return (
    <ThemeProvider>
      <div className="min-h-screen bg-gray-100 dark:bg-gray-900 text-gray-900 dark:text-white transition-colors duration-300">
        {/* Navbar Component */}
        <Navbar />

        {/* Main Content */}
        <div className="container mx-auto px-4 py-8">
          <div className="max-w-2xl mx-auto bg-white dark:bg-gray-800 shadow-lg rounded-lg p-6">
            {/* Response Display Component */}
            <ResponseDisplay 
              conversation={conversation}
              isLoading={isLoading}
            />

            {/* ChatInput Component */}
            <ChatInput 
              onNewQuestion={handleNewQuestion} // Pass function to handle new questions
              setIsLoading={setIsLoading} // Pass loading state
              isGenerating={isGenerating} // Pass text generation state
              onPauseOrTerminate={handlePauseOrTerminate} // Pass function to pause/terminate
              isSpeaking={isSpeaking} // Pass voice input state
              onStopSpeaking={handleStopSpeaking} // Pass function to stop voice input
            />
          </div>
        </div>
      </div>
    </ThemeProvider>
  );
}

export default App;
