import React, { useState, useRef, useEffect } from 'react';
import GeminiService from '../utils/geminiService';

const ChatInput = ({ 
  onNewQuestion, 
  setIsLoading, 
  isGenerating, 
  onPauseOrTerminate 
}) => {
  // State Management
  const [question, setQuestion] = useState('');
  const [attachedFile, setAttachedFile] = useState(null);
  const [generationProgress, setGenerationProgress] = useState(0);
  const fileInputRef = useRef(null);
  const abortControllerRef = useRef(null);

  // File Validation Utility
  const validateFile = (file) => {
    const allowedTypes = [
      'image/jpeg', 
      'image/png', 
      'image/gif', 
      'application/pdf',
      'image/webp',
      'image/svg+xml'
    ];
    const maxSize = 15 * 1024 * 1024; // 15MB
    const minSize = 1 * 1024; // 1KB

    if (!allowedTypes.includes(file.type)) {
      throw new Error('Unsupported file type');
    }

    if (file.size > maxSize) {
      throw new Error('File too large (max 15MB)');
    }

    if (file.size < minSize) {
      throw new Error('File too small');
    }

    return true;
  };

  // Progress Tracking for Generation
  useEffect(() => {
    let progressInterval;
    if (isGenerating) {
      progressInterval = setInterval(() => {
        setGenerationProgress(prev => 
          prev >= 90 ? 90 : prev + Math.random() * 10
        );
      }, 500);
    } else {
      setGenerationProgress(0);
      clearInterval(progressInterval);
    }

    return () => clearInterval(progressInterval);
  }, [isGenerating]);

  // File Handling
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      try {
        validateFile(file);
        setAttachedFile(file);
      } catch (error) {
        alert(error.message);
        e.target.value = null;
      }
    }
  };

  // File Removal
  const handleFileRemove = () => {
    setAttachedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = null;
    }
  };

  // Base64 File Reading
  const readFileAsBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  // Submission Handler
  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Input Validation
    const trimmedQuestion = question.trim();
    if (!trimmedQuestion && !attachedFile) return;

    if (trimmedQuestion.length > 1500) {
      alert('Question is too long. Maximum 1500 characters.');
      return;
    }

    // Abort Controller for Cancellation
    abortControllerRef.current = new AbortController();
    
    setIsLoading(true);
    try {
      let result;
      if (attachedFile) {
        const fileContent = await readFileAsBase64(attachedFile);
        result = await GeminiService.generateAnswerWithFile(
          trimmedQuestion, 
          fileContent,
          { 
            signal: abortControllerRef.current.signal,
            onProgress: (progress) => setGenerationProgress(progress)
          }
        );
      } else {
        result = await GeminiService.generateAnswer(
          trimmedQuestion,
          { 
            signal: abortControllerRef.current.signal,
            onProgress: (progress) => setGenerationProgress(progress)
          }
        );
      }
      
      if (result.success) {
        onNewQuestion(trimmedQuestion, result.answer);
      } else {
        const errorMessage = result.error || 
          "An unexpected error occurred while processing your request.";
        
        console.error("Gemini API Error:", errorMessage);
        onNewQuestion(trimmedQuestion, errorMessage);
      }
    } catch (error) {
      if (error.name === 'AbortError') {
        onNewQuestion(trimmedQuestion, "Generation was terminated by user.");
      } else {
        console.error("Unexpected Error:", error);
        
        const userFriendlyMessage = 
          error.message.includes('API key') 
            ? "Invalid or missing API key. Please check your configuration." 
            : error.message.includes('network')
            ? "Network error. Please check your internet connection."
            : "An unexpected error occurred. Please try again later.";
        
        onNewQuestion(trimmedQuestion, userFriendlyMessage);
      }
    } finally {
      setIsLoading(false);
      setQuestion('');
      handleFileRemove();
      setGenerationProgress(0);
    }
  };

  // Termination Handler
  const handleTerminate = () => {
    if (abortControllerRef.current) {
      abortControllerRef.current.abort();
    }
    onPauseOrTerminate();
  };

  return (
    <form onSubmit={handleSubmit} className="relative">
      <div className="flex items-center space-x-2">
        {/* File Attachment */}
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileChange}
          className="hidden"
          accept="image/jpeg,image/png,image/gif,application/pdf,image/webp,image/svg+xml"
        />
        
        <button
          type="button"
          onClick={() => fileInputRef.current.click()}
          className="p-2 bg-gray-200 dark:bg-gray-700 rounded 
                     hover:bg-gray-300 dark:hover:bg-gray-600 
                     transition-colors duration-300"
          aria-label="Attach File"
        >
          📎
        </button>

        {/* Attached File Display */}
        {attachedFile && (
          <div className="flex items-center bg-blue-50 dark:bg-blue-900/30 px-2 py-1 rounded">
            <span className="text-sm mr-2 text-gray-600 dark:text-gray-300">
              {attachedFile.name}
            </span>
            <button
              type="button"
              onClick={handleFileRemove}
              className="text-red-500 hover:text-red-700"
            >
              ✕
            </button>
          </div>
        )}

        {/* Text Input */}
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Ask a question... (max 1500 chars)"
          maxLength={1500}
          className="flex-grow px-4 py-2 border dark:border-gray-600 
                     focus:outline-none focus:ring-2 focus:ring-blue-500 
                     dark:bg-gray-700"
        />

        {/* Generation Control Button */}
        {isGenerating ? (
          <button
            type="button"
            onClick={handleTerminate}
            className="bg-red-500 hover:bg-red-600 text-white 
                       px-4 py-2 rounded transition-colors flex items-center"
          >
            <span>Stop</span>
            {generationProgress > 0 && (
              <div 
                className="ml-2 w-8 h-1 bg-white/30 rounded-full overflow-hidden"
              >
                <div 
                  className="h-full bg-white" 
                  style={{width: `${generationProgress}%`}}
                ></div>
              </div>
            )}
          </button>
        ) : (
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-600 text-white 
                       px-4 py-2 rounded transition-colors"
          >
            Send
          </button>
        )}
      </div>

      {/* Character Count and Progress */}
      <div className="flex justify-between items-center mt-1">
        {question.length > 0 && (
          <div className="text-xs text-gray-500">
            {question.length}/1500 characters
          </div>
        )}
        
        {isGenerating && generationProgress > 0 && (
          <div className="text-xs text-gray-500">
            Generating... {Math.round(generationProgress)}%
          </div>
        )}
      </div>
    </form>
  );
};

export default ChatInput;
