import React, { useState, useRef, useEffect } from 'react';
import GeminiService from '../utils/geminiService';

const VoiceInput = ({ onNewQuestion, setIsLoading }) => {
  const [isListening, setIsListening] = useState(false);
  const [transcript, setTranscript] = useState('');
  const recognitionRef = useRef(null);

  useEffect(() => {
    // Check browser support for speech recognition
    const SpeechRecognition = 
      window.SpeechRecognition || 
      window.webkitSpeechRecognition;

    if (SpeechRecognition) {
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;
      recognitionRef.current.lang = 'en-US';

      recognitionRef.current.onresult = (event) => {
        const speechResult = event.results[0][0].transcript;
        setTranscript(speechResult);
        handleVoiceSubmit(speechResult);
      };

      recognitionRef.current.onerror = (event) => {
        console.error('Speech recognition error:', event.error);
        setIsListening(false);
      };
    } else {
      console.warn('Speech recognition not supported');
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, []);

  const startListening = () => {
    if (recognitionRef.current) {
      setIsListening(true);
      setTranscript('');
      recognitionRef.current.start();
    } else {
      alert('Speech recognition is not supported in this browser');
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setIsListening(false);
    }
  };

  const handleVoiceSubmit = async (question) => {
    setIsLoading(true);
    try {
      // Text-to-Speech for response
      const result = await GeminiService.generateAnswer(question);
      
      if (result.success) {
        onNewQuestion(question, result.answer);
        // Speak the answer
        speakAnswer(result.answer);
      } else {
        onNewQuestion(question, "Sorry, there was an error processing your request.");
      }
    } catch (error) {
      onNewQuestion(question, "An unexpected error occurred.");
    } finally {
      setIsLoading(false);
      setIsListening(false);
    }
  };

  const speakAnswer = (text) => {
    // Text-to-Speech functionality
    const utterance = new SpeechSynthesisUtterance(text);
    utterance.rate = 0.9; // Adjust speech rate
    window.speechSynthesis.speak(utterance);
  };

  return (
    <div className="flex items-center">
      <button
        onClick={isListening ? stopListening : startListening}
        className={`p-2 rounded-full transition-colors duration-300 
          ${isListening 
            ? 'bg-red-500 hover:bg-red-600 text-white' 
            : 'bg-blue-500 hover:bg-blue-600 text-white'
          }`}
      >
        {isListening ? (
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            className="h-6 w-6" 
            fill="none" 
            viewBox="0 0 24 24" 
            stroke="currentColor"
          >
            <path 
              strokeLinecap="round" 
              strokeLinejoin="round" 
              strokeWidth={2} 
              d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" 
            />
          </svg>
        ) : (
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            className="h-6 w-6" 
            fill="none" 
            viewBox="0 0 24 24" 
            stroke="currentColor"
          >
            <path 
              strokeLinecap="round" 
              strokeLinejoin="round" 
              strokeWidth={2} 
              d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" 
            />
          </svg>
        )}
      </button>

      {isListening && (
        <div className="ml-2 text-sm text-gray-600 dark:text-gray-300">
          Listening...
        </div>
      )}

      {transcript && !isListening && (
        <div className="ml-2 text-sm text-gray-600 dark:text-gray-300">
          Transcribed: {transcript}
        </div>
      )}
    </div>
  );
};

export default VoiceInput;
