import React, { useState, useRef } from 'react';
import GeminiService from '../utils/geminiService';
import VoiceInput from './VoiceInput';

const ChatInput = ({ onNewQuestion, setIsLoading, isGenerating, onPauseOrTerminate, isSpeaking, onStopSpeaking }) => {
  const [question, setQuestion] = useState('');
  const fileInputRef = useRef(null);
  const [attachedFile, setAttachedFile] = useState(null);

  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      const allowedTypes = ['image/jpeg', 'image/png', 'image/gif', 'application/pdf'];
      const maxSize = 5 * 1024 * 1024; // 5MB

      if (allowedTypes.includes(file.type) && file.size <= maxSize) {
        setAttachedFile(file);
      } else {
        alert('Please upload a valid image (JPEG, PNG, GIF) or PDF under 5MB');
        e.target.value = null;
      }
    }
  };

  const handleFileRemove = () => {
    setAttachedFile(null);
    if (fileInputRef.current) {
      fileInputRef.current.value = null;
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!question.trim() && !attachedFile) return;

    setIsLoading(true);
    try {
      let result;
      if (attachedFile) {
        const fileContent = await readFileAsBase64(attachedFile);
        result = await GeminiService.generateAnswerWithFile(
          question, 
          fileContent
        );
      } else {
        result = await GeminiService.generateAnswer(question);
      }
      
      if (result.success) {
        onNewQuestion(question, result.answer);
      } else {
        const errorMessage = result.error || 
          "An unexpected error occurred while processing your request.";
        
        console.error("Gemini API Error:", errorMessage);
        
        onNewQuestion(question, errorMessage);
      }
    } catch (error) {
      console.error("Unexpected Error:", error);
      
      const userFriendlyMessage = 
        error.message.includes('API key') 
          ? "Invalid or missing API key. Please check your configuration." 
          : error.message.includes('network')
          ? "Network error. Please check your internet connection."
          : "An unexpected error occurred. Please try again later.";
      
      onNewQuestion(question, userFriendlyMessage);
    } finally {
      setIsLoading(false);
      setQuestion('');
      handleFileRemove();
    }
  };

  const readFileAsBase64 = (file) => {
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.readAsDataURL(file);
      reader.onload = () => resolve(reader.result);
      reader.onerror = (error) => reject(error);
    });
  };

  return (
    <form onSubmit={handleSubmit} className="mt-4">
      <div className="flex items-center">
        {/* File Attachment Button */}
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileChange}
          className="hidden"
          accept="image/jpeg,image/png,image/gif,application/pdf"
        />
        
        <button
          type="button"
          onClick={() => fileInputRef.current.click()}
          className="mr-2 p-2 bg-gray-200 dark:bg-gray-700 rounded-l-lg 
                     hover:bg-gray-300 dark:hover:bg-gray-600 
                     transition-colors duration-300"
          aria-label="Attach File"
        >
          <svg 
            xmlns="http://www.w3.org/2000/svg" 
            className="h-6 w-6 text-gray-600 dark:text-gray-300" 
            fill="none" 
            viewBox="0 0 24 24" 
            stroke="currentColor"
          >
            <path 
              strokeLinecap="round" 
              strokeLinejoin="round" 
              strokeWidth={2} 
              d="M15.172 7l-6.586 6.586a2 2 0 102.828 2.828l6.414-6.586a4 4 0 00-5.656-5.656l-6.415 6.585a6 6 0 108.486 8.486L20.5 13" 
            />
          </svg>
        </button>

        {/* Voice Input Component */}
        <VoiceInput 
          onNewQuestion={onNewQuestion} 
          setIsLoading={setIsLoading}
          isSpeaking={isSpeaking}
          onStopSpeaking={onStopSpeaking}
        />

        {/* Attached File Display */}
        {attachedFile && (
          <div className="flex items-center bg-blue-50 dark:bg-blue-900/30 px-2 py-1 rounded mr-2">
            <span className="text-sm mr-2 text-gray-600 dark:text-gray-300">
              {attachedFile.name}
            </span>
            <button
              type="button"
              onClick={handleFileRemove}
              className="text-red-500 hover:text-red-700"
            >
              ✕
            </button>
          </div>
        )}

        {/* Text Input */}
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Ask a question..."
          className="flex-grow px-4 py-2 border dark:border-gray-600 
                     focus:outline-none focus:ring-2 focus:ring-blue-500 
                     dark:bg-gray-700"
        />

        {/* Send Button */}
        <button
          type="submit"
          className={`px-4 py-2 rounded-r-lg transition-colors duration-300 ${
            isGenerating
              ? 'bg-red-500 hover:bg-red-600'
              : 'bg-blue-500 hover:bg-blue-600'
          } text-white`}
        >
          {isGenerating ? 'Stop' : 'Send'}
        </button>
      </div>
    </form>
  );
};

export default ChatInput;
