import React, { useState, useRef, useEffect } from 'react';
import GeminiService from '../utils/geminiService';

const ChatInput = ({ 
  onNewQuestion, 
  setIsLoading, 
  isGenerating, 
  onPauseOrTerminate 
}) => {
  // State Management
  const [question, setQuestion] = useState('');
  const [attachedFile, setAttachedFile] = useState(null);
  const [generationProgress, setGenerationProgress] = useState(0);
  const [isVoiceListening, setIsVoiceListening] = useState(false);
  const fileInputRef = useRef(null);
  const abortControllerRef = useRef(null);
  const speechRecognitionRef = useRef(null);

  // Voice Recognition Setup
  useEffect(() => {
    const SpeechRecognition = 
      window.SpeechRecognition || 
      window.webkitSpeechRecognition;

    if (SpeechRecognition) {
      speechRecognitionRef.current = new SpeechRecognition();
      speechRecognitionRef.current.continuous = false;
      speechRecognitionRef.current.interimResults = false;
      speechRecognitionRef.current.lang = 'en-US';

      speechRecognitionRef.current.onresult = (event) => {
        const voiceInput = event.results[0][0].transcript;
        setQuestion(prevQuestion => prevQuestion + ' ' + voiceInput.trim());
        setIsVoiceListening(false);
      };

      speechRecognitionRef.current.onerror = (event) => {
        console.error('Voice recognition error:', event.error);
        setIsVoiceListening(false);
      };

      speechRecognitionRef.current.onend = () => {
        setIsVoiceListening(false);
      };
    }

    return () => {
      if (speechRecognitionRef.current) {
        speechRecognitionRef.current.stop();
      }
    };
  }, []);

  // Voice Input Handler
  const handleVoiceInput = () => {
    if (speechRecognitionRef.current) {
      if (isVoiceListening) {
        // Stop listening if already listening
        speechRecognitionRef.current.stop();
        setIsVoiceListening(false);
      } else {
        try {
          // Start listening
          speechRecognitionRef.current.start();
          setIsVoiceListening(true);
        } catch (error) {
          console.error('Failed to start voice recognition:', error);
          alert('Voice input is not available. Check microphone permissions.');
        }
      }
    } else {
      alert('Voice input is not supported in this browser.');
    }
  };

  // Existing methods... (validateFile, handleFileChange, etc.)
  // ... [Keep all previous methods from the original implementation]

  return (
    <form onSubmit={handleSubmit} className="relative">
      <div className="flex items-center space-x-2">
        {/* File Attachment Button */}
        <input
          type="file"
          ref={fileInputRef}
          onChange={handleFileChange}
          className="hidden"
          accept="image/jpeg,image/png,image/gif,application/pdf,image/webp,image/svg+xml"
        />
        
        <button
          type="button"
          onClick={() => fileInputRef.current.click()}
          className="p-2 bg-gray-200 dark:bg-gray-700 rounded 
                     hover:bg-gray-300 dark:hover:bg-gray-600 
                     transition-colors duration-300"
          aria-label="Attach File"
        >
          ðŸ“Ž
        </button>

        {/* Attached File Display */}
        {attachedFile && (
          <div className="flex items-center bg-blue-50 dark:bg-blue-900/30 px-2 py-1 rounded">
            <span className="text-sm mr-2 text-gray-600 dark:text-gray-300">
              {attachedFile.name}
            </span>
            <button
              type="button"
              onClick={handleFileRemove}
              className="text-red-500 hover:text-red-700"
            >
              âœ•
            </button>
          </div>
        )}

        {/* Text Input */}
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Ask a question... (max 1500 chars)"
          maxLength={1500}
          className="flex-grow px-4 py-2 border dark:border-gray-600 
                     focus:outline-none focus:ring-2 focus:ring-blue-500 
                     dark:bg-gray-700"
        />

        {/* Microphone Button */}
        <button
          type="button"
          onClick={handleVoiceInput}
          className={`p-2 rounded transition-colors duration-300 
            ${isVoiceListening 
              ? 'bg-red-500 hover:bg-red-600 text-white' 
              : 'bg-gray-200 dark:bg-gray-700 hover:bg-gray-300'
            }`}
          aria-label="Voice Input"
        >
          {isVoiceListening ? (
            <svg 
              xmlns="http://www.w3.org/2000/svg" 
              className="h-6 w-6" 
              fill="none" 
              viewBox="0 0 24 24" 
              stroke="currentColor"
            >
              <path 
                strokeLinecap="round" 
                strokeLinejoin="round" 
                strokeWidth={2} 
                d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0 0H8m4 0h4m-4-8a3 3 0 01-3-3V5a3 3 0 116 0v6a3 3 0 01-3 3z" 
              />
            </svg>
          ) : (
            <svg 
              xmlns="http://www.w3.org/2000/svg" 
              className="h-6 w-6" 
              fill="none" 
              viewBox="0 0 24 24" 
              stroke="currentColor"
            >
              <path 
                strokeLinecap="round" 
                strokeLinejoin="round" 
                strokeWidth={2} 
                d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" 
              />
            </svg>
          )}
        </button>

        {/* Generation Control Button */}
        {isGenerating ? (
          <button
            type="button"
            onClick={handleTerminate}
            className="bg-red-500 hover:bg-red-600 text-white 
                       px-4 py-2 rounded transition-colors flex items-center"
          >
            <span>Stop</span>
            {generationProgress > 0 && (
              <div 
                className="ml-2 w-8 h-1 bg-white/30 rounded-full overflow-hidden"
              >
                <div 
                  className="h-full bg-white" 
                  style={{width: `${generationProgress}%`}}
                ></div>
              </div>
            )}
          </button>
        ) : (
          <button
            type="submit"
            className="bg-blue-500 hover:bg-blue-600 text-white 
                       px-4 py-2 rounded transition-colors"
          >
            Send
          </button>
        )}
      </div>

      {/* Status and Character Count */}
      <div className="flex justify-between items-center mt-1">
        {question.length > 0 && (
          <div className="text-xs text-gray-500">
            {question.length}/1500 characters
          </div>
        )}
        
        {isVoiceListening && (
          <div className="text-xs text-blue-500">
            Listening... Speak now
          </div>
        )}

        {isGenerating && generationProgress > 0 && (
          <div className="text-xs text-gray-500">
            Generating... {Math.round(generationProgress)}%
          </div>
        )}
      </div>
    </form>
  );
};

export default ChatInput;
