// src/components/ChatInput.js
import React, { useState } from 'react';
import GeminiService from '../utils/geminiService';

const ChatInput = ({ onNewQuestion, setIsLoading }) => {
  const [question, setQuestion] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!question.trim()) return;

    setIsLoading(true);
    try {
      const result = await GeminiService.generateAnswer(question);
      
      if (result.success) {
        onNewQuestion(question, result.answer);
      } else {
        onNewQuestion(question, "Sorry, there was an error processing your request.");
      }
    } catch (error) {
      onNewQuestion(question, "An unexpected error occurred.");
    } finally {
      setIsLoading(false);
      setQuestion('');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="mt-4">
      <div className="flex items-center">
        <input
          type="text"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          placeholder="Ask a question..."
          className="flex-grow px-4 py-2 border dark:border-gray-600 rounded-l-lg focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700"
        />
        <button
          type="submit"
          className="bg-blue-500 hover:bg-blue-600 text-white px-4 py-2 rounded-r-lg transition-colors"
        >
          Send
        </button>
      </div>
    </form>
  );
};

export default ChatInput;
