// src/components/ResponseDisplay.js
import React from 'react';

const ResponseDisplay = ({ conversation, isLoading }) => {
  const copyToClipboard = (text) => {
    navigator.clipboard.writeText(text);
  };

  return (
    <div className="space-y-4 max-h-[500px] overflow-y-auto">
      {conversation.map((entry, index) => (
        <div key={index} className="mb-4">
          <div className="bg-gray-100 dark:bg-gray-700 p-3 rounded-lg mb-2">
            <p className="font-semibold text-gray-800 dark:text-white">
              {entry.question}
            </p>
          </div>
          <div className="relative bg-blue-50 dark:bg-blue-900 p-3 rounded-lg">
            <p className="text-gray-700 dark:text-gray-200">
              {entry.answer}
            </p>
            <button 
              onClick={() => copyToClipboard(entry.answer)}
              className="absolute top-2 right-2 bg-blue-500 text-white px-2 py-1 rounded text-xs hover:bg-blue-600"
            >
              Copy
            </button>
          </div>
        </div>
      ))}
      {isLoading && (
        <div className="text-center text-gray-500 dark:text-gray-400">
          Generating response...
        </div>
      )}
      {conversation.length === 0 && (
        <div className="text-center text-gray-500 dark:text-gray-400">
          Ask your first question!
        </div>
      )}
    </div>
  );
};

export default ResponseDisplay;
