import React from 'react';

const VoiceInput = ({ onNewQuestion, setIsLoading, isSpeaking, onStopSpeaking }) => {
  const handleVoiceInput = () => {
    if (isSpeaking) {
      onStopSpeaking(); // Stop speaking if already speaking
    } else {
      // Start voice input logic here
      setIsLoading(true);
      // Simulate voice input for demonstration
      setTimeout(() => {
        const simulatedQuestion = "This is a simulated voice input question.";
        onNewQuestion(simulatedQuestion, "This is a simulated answer.");
        setIsLoading(false);
      }, 2000);
    }
  };

  return (
    <button
      type="button"
      onClick={handleVoiceInput}
      className={`p-2 rounded-lg transition-colors duration-300 ${
        isSpeaking ? 'bg-red-500 hover:bg-red-600' : 'bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600'
      }`}
      aria-label="Voice Input"
    >
      <svg 
        xmlns="http://www.w3.org/2000/svg" 
        className={`h-6 w-6 ${
          isSpeaking ? 'text-white' : 'text-gray-600 dark:text-gray-300'
        }`} 
        fill="none" 
        viewBox="0 0 24 24" 
        stroke="currentColor"
      >
        <path 
          strokeLinecap="round" 
          strokeLinejoin="round" 
          strokeWidth={2} 
          d="M19 11a7 7 0 01-7 7m0 0a7 7 0 01-7-7m7 7v4m0-4H3m15 0h3m-3-7a7 7 0 00-7-7m0 0a7 7 0 00-7 7m7-7v4m0-4h14m-14 0H3" 
        />
      </svg>
    </button>
  );
};

export default VoiceInput;
